import React, { useState, useEffect } from "react";
import ReactMapboxGl, {Layer, Feature, Marker} from "react-mapbox-gl";
import svg from './Pin'
// tslint:disable-next-line:no-var-requires
const { token, styles } = require('./config.json');

const Map = ReactMapboxGl({ accessToken: token });

const mapStyle = {
  flex: 1,
  height: 500
};

const paintLayer = {
  'fill-extrusion-color': '#aaa',
  'fill-extrusion-height': {
    type: 'identity',
    property: 'height'
  },
  'fill-extrusion-base': {
    type: 'identity',
    property: 'min_height'
  },
  'fill-extrusion-opacity': 0.6
};

// export interface Props {
//   // tslint:disable-next-line:no-any
//   onStyleLoad?: (map: any) => any;
// }




// Create an image for the Layer
const image = new Image();
image.src = 'data:image/svg+xml;charset=utf-8;base64,'+btoa(svg)
const images = ['pin', image]

const getStations = () =>{
  return {
    "a": {
      "position": [-80.544861, 43.472286]
    },
    "b": {
      "position": [43.472286, -80.544861]
    },
  }
}

function ThreeDMap(props) {

  const [fitBounds, setFitBounds] = useState(undefined);
  const [center, setCenter] = useState([-80.544861, 43.472286]);
  const [zoom, setZoom] = useState([50]);
  const [bearing, setBearing] = useState([-60]);
  const [pitch, setPitch] = useState([80]);
  const [station, setStation] = useState(undefined);
  const [stations, setStations] = useState({});


  // Define layout to use in Layer component
  const layoutLayer = { 'icon-image': 'pin' };

  useEffect(()=> {


    var k = getStations();
    setStations(k)

  }, []);

  // onStyleLoad = (map) => {
  //   // const onStyleLoad = props.onStyleLoad;
  //   return props.onStyleLoad && props.onStyleLoad(map);
  // };


    return (
      <div>
      <Map
        style="mapbox://styles/mapbox/streets-v8"
        containerStyle={mapStyle}
        onStyleLoad={props.onStyleLoad}
        zoom={zoom}
        center={center}
        pitch={pitch}
        bearing={bearing}
      >
        <Layer
          id="3d-buildings"
          sourceId="composite"
          sourceLayer="building"
          filter={['==', 'extrude', 'true']}
          type="fill-extrusion"
          minZoom={14}
          paint={paintLayer}
        />
        <Layer 
          type="symbol" 
          id="marker" 
          layout={layoutLayer} 
          images={images}
        >
          {Object.keys(stations).map((stationK, index)=> (
            <Feature
              key= {stationK}
              coordinates={stations[stationK].position}
            />
          ))}
        </Layer>



      </Map>

      <div>Icons made by <a href="https://www.flaticon.com/authors/smashicons" title="Smashicons">Smashicons</a> from <a href="https://www.flaticon.com/" title="Flaticon">www.flaticon.com</a></div>
      </div>
    );

}

export default ThreeDMap;
