import React, { useState, useEffect } from "react";
import ReactMapboxGl, {Layer, Feature, Marker} from "react-mapbox-gl";
import svg from './pin.svg'
// tslint:disable-next-line:no-var-requires
const { token, styles } = require('./config.json');

const Map = ReactMapboxGl({ accessToken: token });

const mapStyle = {
  flex: 1,
  height: 500
};

const paintLayer = {
  'fill-extrusion-color': '#aaa',
  'fill-extrusion-height': {
    type: 'identity',
    property: 'height'
  },
  'fill-extrusion-base': {
    type: 'identity',
    property: 'min_height'
  },
  'fill-extrusion-opacity': 0.6
};

// export interface Props {
//   // tslint:disable-next-line:no-any
//   onStyleLoad?: (map: any) => any;
// }

  // Define layout to use in Layer component
const layoutLayer = { 'icon-image': 'marker' };

// Create an image for the Layer
const image = new Image();
image.src = 'data:image/svg+xml;charset=utf-8;base64,' + btoa(svg);
const images = ['marker', svg];

const getStations = () =>{
  return {
    "a": {
      "position": [-80.544861, 43.472286]
    },
    "b": {
      "position": [-80.6, 43.5]
    },
  }
}

function ThreeDMap(props) {

  const [fitBounds, setFitBounds] = useState(undefined);
  const [center, setCenter] = useState([-80.544861, 43.472286]);
