  // };


    return (
      <Map
        style="mapbox://styles/mapbox/streets-v8"
        containerStyle={mapStyle}
        onStyleLoad={props.onStyleLoad}
        zoom={zoom}
        center={center}
        pitch={pitch}
        bearing={bearing}
      >
        <Layer
          id="3d-buildings"
          sourceId="composite"
          sourceLayer="building"
          filter={['==', 'extrude', 'true']}
          type="fill-extrusion"
          minZoom={14}
          paint={paintLayer}
        />
        <Layer 
          type="symbol" 
          id="marker" 
          // layout={layoutLayer} 
          images={images}
        >
          {Object.keys(stations).map((stationK, index)=> (
            <Feature
              key= {stationK}
              coordinates={stations[stationK].position}
            />
          ))}
        </Layer>


      </Map>
    );

}

export default ThreeDMap;
